import React, { Component } from "react";
import axios from "axios";
import { Container, Form, Button } from "react-bootstrap";

export class CreateRequest extends Component {
  constructor(props) {
    super();
    this.state = {
      title: "",
      description: "",
      selectCategory: "",
      // startDate: "",
      // endDate: "",
      // addLocation: "",
    };
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeSelectCategory = this.onChangeSelectCategory.bind(this);
    // this.onChangeStartDate = this.onChangeStartDate.bind(this);
    // this.onChangeEndDate = this.onChangeEndDate.bind(this);
    // this.onAddLocation = this.onAddLocation.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  onChangeTitle(currObj) {
    console.log(currObj.target.value);
    this.setState({ title: currObj.target.value });
  }

  onChangeDescription(currObj) {
    console.log(currObj.target.value);
    this.setState({ description: currObj.target.value });
  }

  onChangeSelectCategory(currObj) {
    console.log(currObj.target.value);
    this.setState({ selectCategory: currObj.target.value });
  }

  // onChangeStartDate(currObj) {
  //   console.log(currObj.target.value);
  //   this.setState({ startDate: currObj.target.value });
  // }
  // onChangeEndDate(currObj) {
  //   console.log(currObj.target.value);
  //   this.setState({ endDate: currObj.target.value });
  // }

  // onChangeOnAddLocation(currObj) {
  //   console.log(currObj.target.value);
  //   this.setState({ addLocation: currObj.target.value });
  // }

  handleSubmit(e) {
    e.preventDefault();

    console.log("## " + this.state.title);
    const userPostRequest = {
      title: this.state.title,
      description: this.state.description,
      selectCategory: this.state.selectCategory,
      // startDate: this.state.startDate,
      // endtDate: this.state.endDate,
    };
    console.log("TEST:" + JSON.stringify(userPostRequest));

    axios
      .post("http://localhost:5000/createRequest", userPostRequest)
      .then((res) => console.log("****" + res.data));
    this.setState({
      title: "",
      description: "",
      selectCategory: "",
      // startDate: "",
      // endDate: "",
    });
  }

  render() {
    return (
      <div>
        <h1>Post Request</h1>
        <div>
          <Container>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group>
                <Form.Label>Title:</Form.Label>
                <Form.Control
                  type="title"
                  value={this.state.title}
                  onChange={this.onChangeTitle}
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Description:</Form.Label>

                <Form.Control
                  type="description"
                  value={this.state.description}
                  onChange={this.onChangeDescription}
                />
              </Form.Group>

              <Form.Group >
                <Form.Label>Select Category</Form.Label>
                <Form.Select onSelect={this.onChangeSelectCategory} value={this.state.selectCategory}>
                  <option value="1">Electrician</option>
                  <option value="2">Plumber</option>
                  <option value="3">Carpenter</option>
                  <option value="4">Hairs Stylist and Barbers</option>
                  <option value="5">Beautician</option>
                  <option value="6">Bakers</option>
                  <option value="7">Nail Technician</option>
                  <option value="8">Painter</option>
                  <option value="9">Chef</option>
                  <option value="10">Cleaner</option>
                </Form.Select>
              </Form.Group>
              {/* <Form.Group>
                
                <Form.Label as="legend"  />
                <Form.Check
                  inline
                  type="radio"
                  label="Free"
                  name="cost"
                  id="freeId"
                />
                <Form.Check
                  inline
                  type="radio"
                  label="Please Contact"
                  name="cost"
                  id="pleaseContact"
                />
                <Form.Check
                  inline
                  type="radio"
                  name="cost"
                  label="Price"
                  id="price"
                />
              </Form.Group> */}
              {/* <Form.Group>
                <Form.Label as="legend">Select date:</Form.Label>
              </Form.Group>
              <Form.Group className="">
                <Form.Label as="legend">Start date</Form.Label>
                <Form.Control type="date"  
                value={this.state.startDate}
                onChange={this.onChangeStartDate}

                />
                <Form.Label as="legend">End date</Form.Label>
                <Form.Control type="date"
                 value={this.state.endDate}
                 onChange={this.onChangeEndDate}
                 />
              </Form.Group> */}
              {/* <Form.Group >
                <Form.Label>Add Location:</Form.Label>
                <Form.Control type="addLocation"
                value={this.state.addLocation}
                onChange={this.state.onChangeOnAddLocation}
                
                />
              </Form.Group> */}
              <Button type="submit">Submit</Button>
            </Form>
          </Container>
        </div>
      </div>
    );
  }
}
