import React, { Component } from "react";
import axios from "axios";
import { Container, Form, Button } from "react-bootstrap";

export class CreateRequest extends Component {
  constructor(props) {
    super();
    this.state = {
      title: "",
      description: "",
    };
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  onChangeTitle(currObj) {
    console.log(currObj.target.value);
    this.setState({ title: currObj.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();

    console.log("## " + this.state.title)
    const userPostRequest = {
      title: this.state.title,
    };
    console.log("TEST:" + JSON.stringify(userPostRequest));

    axios
      .post("http://localhost:5000/createRequest", userPostRequest)
      .then((res) => console.log("****" + res.data));
    this.setState({
      title: "",
    });
  }

  render() {
    return (
      <div>
        <h1>Post Request</h1>
        <div className="col-4">
          <Container>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group>
                <Form.Label>Title:</Form.Label>
                {/* <Form.Control type="text" onChange={(e) => setTitle(e.target.value)}  value={title}/> */}

                <Form.Control
                  type="title"
                  value={this.state.title}
                  onChange={this.onChangeTitle}
                />
              </Form.Group>
              <Button type="submit">Submit</Button>
              {/* <input className="userInput" type="submit" value="Register" /> */}
            </Form>
          </Container>
        </div>
      </div>
    );
  }
}
