import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Container, Form, Button } from "react-bootstrap";

export class CreateRequest extends Component {
 constructor(props){
    super();
    this.state ={
        title: ""
    }
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
 }

 onChangeTitle(e) {
    this.setState({title: e.target.value})
 }

 onSubmit(e) {
   
        e.preventDefault();
        const request = {
            title: this.setState.title,

    }
    console.log(title);
 

   axios.post('http://localhost:5000/createRequest', request)
.then(res => console.log(res.data));
this.setState({
title: ''
})
}





    render() {
        return (
            <div>
                 <h1>Post Request</h1>
                <div className="col-4">
        <Container>
          <Form onSubmit={this.onSubmit} >
            <Form.Group className="formInput">
              <Form.Label>Title:</Form.Label>
              {/* <Form.Control type="text" onChange={(e) => setTitle(e.target.value)}  value={title}/> */}

              <Form.Control
            type="text"
            value={this.state.title}
            onChange={this.onChangeTitle }
            
            />
            </Form.Group>
            <Button type="submit">Submit</Button>
            {/* <input className="userInput" type="submit" value="Register" /> */}
          </Form>
        </Container>
      </div>
      <Footer />
                
            </div>
        );
    }
}
