import React, { Component } from "react";
import axios from "axios";
import { Container, Form, Button } from "react-bootstrap";

export class CreateRequest extends Component {
  constructor(props) {
    super();
    this.state = {
      title: "",
    }
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onChangeTitle(e) {
    this.setState({ title: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();
    const request = {
      title: this.state.title,
    };
    console.log(title);

    axios
      .post("http://localhost:5000/createRequest", request)
      .then((res) => console.log(res.data));
    this.setState({
      title: ""
    })
  }

  render() {
    return (
      <div>
        <h1>Post Request</h1>
        <div className="col-4">
          <Container>
            <Form >
              <Form.Group onSubmit={this.onSubmit}>
                <Form.Label>Title:</Form.Label>
                {/* <Form.Control type="text" onChange={(e) => setTitle(e.target.value)}  value={title}/> */}

                <Form.Control
                  type="title"
                  value={this.state.title}
                  onChange={this.onChangeTitle}
                />
              </Form.Group>
              <Button type="submit">Submit</Button>
              {/* <input className="userInput" type="submit" value="Register" /> */}
            </Form>
          </Container>
        </div>
        
      </div>
    );
  }
}
