import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Button, Container, Table, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

import { NavBar } from "../../components/sections/navBar";
import { Footer } from "../../components/sections/footer";

// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { solid, regular, brands, icon } from '@fortawesome/fontawesome-svg-core/import.macro'

export function NearByRequests() {
  const [usrRequests, setUsrRequests] = useState([]);

  useEffect(function () {
    getUsrRequestsList();
  }, []);

  async function getUsrRequestsList() {
    try {
      const response = await axios.get(
        "http://localhost:5000/getOtherPeopleRequests"
      );

      if (response.data.statusCode === "0") {
        setUsrRequests(response.data.reqResults);
      } else {
        console.log("Some thing went wrong.....Required error handling...");
      }
    } catch (error) {
      console.log("error", error);
    }
  }

  return (
    <div>
      <NavBar />
      <Container fluid="md">
        <div class="kk">
          <h1>Near By Requests</h1>
        </div>

        {/* <Table striped bordered hover responsive="sm"> */}
          {/* <thead>
            <tr>
              <th className="TableHeading">Title</th>
              <th>Description</th>
              <th>Category</th>

              <th>Cost</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Location</th>
            </tr>
          </thead> */}
          {/* <tbody> */}
            {usrRequests.map((usrRequest) => (
              <>
                <Row>
                  <div>
                    <div>
                      <strong>Title: </strong>
                      {usrRequest.title}
                    </div>
                    <Row>
                      <Col md={4}>
                        <strong>category: </strong>

                        {usrRequest.category}
                      </Col>
                      <Col md={{ span: 4, offset: 4 }}>
                        <strong>cost: </strong>

                        {usrRequest.cost}
                      </Col>
                      <Col md={6}>
                        <strong>sdfdsf: </strong>

                        
                      </Col>
                    </Row>
                    <Row>
                      <Col md={4}>
                        <strong>Start Date: </strong>
                        {usrRequest.startDate}
                      </Col>
                      <Col md={{ span: 4, offset: 4 }}>
                        <strong>End Date: </strong>
                        {usrRequest.endDate}
                      </Col>
                    </Row>

                    <Row>
                      <Col sm={4}>
                        <strong>Description: </strong>
                      </Col>
                      <Col md={{ span: 8, offset: 1 }}>
                        {usrRequest.description}
                      </Col>
                    </Row>

                    <Row>
                      <Col md={4}>
                        <strong>Address: </strong>
                        {usrRequest.locationAddress}
                      </Col>
                    </Row>
                   
                  </div>

                  {/* // <tr>
            //   <td>{usrRequest.cost}</td> */}
                </Row>
                <hr />
              </>
            ))}
          {/* </tbody>
        </Table> */}
      </Container>
      <Footer />
    </div>
  );
}
